extension BusinessHomeViewController: UITableViewDataSource, UITableViewDelegate {
    
    func numberOfSections(in tableView: UITableView) -> Int {
        return 2
    }
    
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        guard let listSection = TableSection(rawValue: section) else { return 0 }
        switch listSection {
        case .userList:
            return presenter.getTotalList()
        case .loader:
            return presenter.getTotalList() >= 20 ? 1 : 0
        }
        
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        guard let section = TableSection(rawValue: indexPath.section) else { return UITableViewCell() }
        switch section {
        case .userList:
            guard let cell = tableView.dequeueReusableCell(withIdentifier: "BusinessCollectionCell", for: indexPath) as? BusinessTableCell else { return UITableViewCell() }
            
            let data = presenter.getBusiness(index: indexPath.row)
            DispatchQueue.main.async {
                cell.updateView(data: data)
                cell.selectionStyle = .none
            }
        case .loader:
            let cell = UITableViewCell(style: .value1, reuseIdentifier: "Cell")
            cell.textLabel?.text = "Please wait.."
            cell.textLabel?.textColor = .lightGray
            return cell
        }
        return UITableViewCell()
    }
    
    func tableView(_ tableView: UITableView, heightForRowAt indexPath: IndexPath) -> CGFloat {
        return 80
    }
    
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        presenter.goToDetailBusiness(index: indexPath.row)
    }
    
}

extension BusinessHomeViewController {

    func tableView(_ tableView: UITableView, willDisplay cell: UITableViewCell, forRowAt indexPath: IndexPath) {
        guard let section = TableSection(rawValue: indexPath.section) else { return }
        guard presenter.getTotalList() != 0 else { return }

        if section == .loader {
            print("load new data..")
            presenter.getNextPage()
        }
    }

    private func hideBottomLoader() {
        DispatchQueue.main.async {
            let lastListIndexPath = IndexPath(row: self.presenter.getTotalList() - 21, section: TableSection.userList.rawValue)
            self.tableView.scrollToRow(at: lastListIndexPath, at: .bottom, animated: true)
        }
    }
    
}

